targets.rawSql:
SELECT b.name as container, s.name as subscription, b.storage_account, b.public_access, b.deleted, b.created_at, b.updated_at FROM az_blob_container as b
INNER JOIN az_subscription AS s ON b.subscription_id = s.subscription_id
WHERE s.name IN ($az_subscription_name);

SELECT COUNT(*) FROM az_blob_container as b
INNER JOIN az_subscription AS s ON b.subscription_id = s.subscription_id
WHERE s.name IN ($az_subscription_name);

SELECT sa.name as storage_account_name, sa.resource_group, s.name as subscription, sa.location, sa.creation_time, sa.created_at, sa.updated_at FROM az_storage_account as sa
INNER JOIN az_subscription AS s ON sa.subscription_id = s.subscription_id
WHERE s.name IN ($az_subscription_name);

SELECT COUNT(sa.id) from az_storage_account as sa
INNER JOIN az_subscription as s on sa.subscription_id = s.subscription_id
WHERE s.name IN ($az_subscription_name);
